function run(ticket, result){
    ticket = JSON.parse(ticket);
    result = JSON.parse(result);
    var tipoBusca = "Email"; // Op√ß√µes V√°lidas: Email ou Nome
    let attendantFound;

    if(result.resource && (result.resource.total >= 1)){
        attendantFound = result.resource.items.find((attendant) => (attendant.email === ticket.atendente));
    }
    if(attendantFound){
        if(tipoBusca === "Nome"){
            var nome = attendantFound.fullName.split(' ');
            if(nome.lenght > 1){
                return `${nome[0]} ${nome[1]}`;
            }
            return nome[0];
        }
        else {
            return attendantFound.email.split('@', 1)[0];
        }
    }
    return '';
}

function run(statusCode, result){
    if(statusCode != 200){
        return '';
    }

    result = JSON.parse(result);
    var tickets = (result.resource) ? result.resource.items : [];
    var openTickets = tickets.filter((t) => !t.closed);
    if(openTickets.length){
        return openTickets[0];
    }

    return '';
}

function run(statusCode, result, lastOpenTicket){
    if((lastOpenTicket == '') || (statusCode != 200)){
        return false;
    }

    result = JSON.parse(result);
    if(result.resource){
        return result.resource.status.includes('Closed');
    }

    return false;
}

