function run(statusCode, result, ticket){
    if(statusCode != 200){
        return '';
    }

    ticket = JSON.parse(ticket);
    result = JSON.parse(result);
    var tipoTexto = 'html'; // as op√ß√µes s√£o: html e plain

    // Quando o parametro recebido √© grande, o Blip passa um obj. {"REDUCED": true}
    if(result.REDUCED){
        var texto =
        `<b>[REDUCED] Conte√∫do muito grande, n√£o suportado pelo Blip</b>.<br>`+
        `Verifique o hist√≥rico de atendimento para visular as mensagens.<br>`;
        texto += (ticket.atendente) ? `<br>Atendido por: ${ticket.atendente}` : ``;

        if(tipoTexto != 'html'){
            texto = texto.replace(/<\/?b>/g, '').replace(/<br>/g, '\n');
        }

        return {
            "msgRef": "",
            "texto": texto
        };
    }

    if(result.resource && (result.resource.total > 0)){
        var itensConversa = result.resource.items;

        // Extrai os itens referentes ao ticket
        var iniIndex = 0 // o 1¬∫ item da array ser√° a √∫ltima mensagem (mais recente) do bloco de mensagens
        var fimIndex = itensConversa.findIndex((item) => (item.type == 'application/vnd.iris.ticket+json'));
        fimIndex = (fimIndex == -1) ? itensConversa.length : fimIndex;
        var itensConversaTicket = itensConversa.slice(iniIndex, fimIndex+1).reverse(); //extrai itens do ticket e os inverte, deixando da msg(item) mais antiga para mais nova

        // Tranforma os itens da conversa em itens de texto e define a msg de refer√™ncia para continua√ß√£o da busca
        var itemRef = itensConversaTicket[0];
        var msgRef = '';
        var agente = {
            "received": {undefined: "Cliente"},
            "sent": {undefined: "Bot", "Human": "Atendente"}
        };
        if(tipoTexto == 'html'){
            var texto = transform_to_html_text(itensConversaTicket, agente, ticket.atendente);
        }
        else{
            var texto = transform_to_simple_text(itensConversaTicket, agente, ticket.atendente);
        }
        if((itemRef.type != 'application/vnd.iris.ticket+json') || itemRef.content.parentSequentialId){
            msgRef = itemRef.id;
        };

        return {
            "msgRef": msgRef,
            "texto": texto
        };
    }

    return {
        "msgRef": "",
        "texto": ""
    };
}

function transform_to_simple_text(itens, mapaAgente, atendente=''){
    var conversaTicketStr = itens.map((item) => {
        if(item.type == 'text/plain'){
            return `${mapaAgente[item.direction][item.metadata["#messageEmitter"]]}: ${item.content}`;
        }
        else if(item.type == 'application/vnd.iris.ticket+json'){
            var ticketNo = `Ticket #${item.content.sequentialId}`;
            ticketNo += (item.content.parentSequentialId) ? ` Mestre(Ticket #${item.content.parentSequentialId})\n`: `\n`;
            return ticketNo;
        }
        else if(item.type == 'application/vnd.lime.media-link+json'){
            var midia =  `${mapaAgente[item.direction][item.metadata["#messageEmitter"]]}: ${item.content.type} (${item.content.uri})`;
            midia += (item.content.text) ? `\n${item.content.text}` : ``;
            return midia;
        }
        else{
            return `${mapaAgente[item.direction][item.metadata["#messageEmitter"]]}: [conte√∫do din√¢mico]`;
        }
    });

    if(atendente){
        conversaTicketStr[conversaTicketStr.length] = `\nAtendido por: ${atendente}`;
    }

    return conversaTicketStr.join('\n'); // Une os textos com uma quebra de linha
}

function transform_to_html_text(itens, mapaAgente, atendente=''){
    var conversaTicketStr = itens.map((item) => {
        if(item.type == 'text/plain'){
            return `<b>${mapaAgente[item.direction][item.metadata["#messageEmitter"]]}:</b> ${item.content}`;
        }
        else if(item.type == 'application/vnd.iris.ticket+json'){
            var ticketNo = `<b>Ticket #${item.content.sequentialId}</b>`;
            ticketNo += (item.content.parentSequentialId) ? ` - Mestre(Ticket #${item.content.parentSequentialId})<br>`: `<br>`;
            return ticketNo;
        }
        else if(item.type == 'application/vnd.lime.media-link+json'){
            var midia =  `<b>${mapaAgente[item.direction][item.metadata["#messageEmitter"]]}:</b> ${item.content.type} (${item.content.uri})`;
            midia += (item.content.text) ? `<br>${item.content.text}` : ``;
            return midia;
        }
        else{
            return `<b>${mapaAgente[item.direction][item.metadata["#messageEmitter"]]}:</b> [conte√∫do din√¢mico]`;
        }
    });

    if(atendente){
        conversaTicketStr[conversaTicketStr.length] = `<br>Atendido por: ${atendente}`;
    }

    return conversaTicketStr.join('<br>'); // Une os textos com uma quebra de linha
}

