function run(horarios, mensagemAtendimento) {

  if(!mensagemAtendimento){
    var mensagemAtendimento = ""
  }

  var horaAtual = new Date();
  var hora = horaAtual.getHours();
  var minutos = horaAtual.getMinutes();
  var horarios = JSON.parse(horarios);
  var { inicio: almocoInicio, fim: almocoFim } = horarios.horarioAlmoco;
  var { inicio: atendimentoInicio, fim: atendimentoFim } = horarios.horarioAtendimento;

  var [almocoInicioHora, almocoInicioMinutos] = almocoInicio.split(":").map(Number);
  var [almocoFimHora, almocoFimMinutos] = almocoFim.split(":").map(Number);
  var [atendimentoInicioHora, atendimentoInicioMinutos] = atendimentoInicio.split(":").map(Number);
  var [atendimentoFimHora, atendimentoFimMinutos] = atendimentoFim.split(":").map(Number);

  if ((hora === almocoInicioHora && minutos >= almocoInicioMinutos) ||
    (hora > almocoInicioHora && hora < almocoFimHora) ||
    (hora === almocoFimHora && minutos <= almocoFimMinutos)) {
    return `Estamos em hor√°rio de almo√ßo (das ${almocoInicio} √†s ${almocoFim}), mas n√£o se preocupe, assim que poss√≠vel nosso time ir√° atend√™-lo (a).`;
  } else if (hora < atendimentoInicioHora ||
    hora === atendimentoInicioHora && minutos < atendimentoInicioMinutos ||
    hora >= atendimentoFimHora ||
    hora === atendimentoFimHora && minutos >= atendimentoFimMinutos) {
    return `Nosso atendimento √© das ${atendimentoInicio} √†s ${atendimentoFim}, mas n√£o se preocupe, assim que poss√≠vel nosso time ir√° atend√™-lo (a).`;
  } else {
    return mensagemAtendimento;
  }
}

