function run(contactIdentity){
    var source = contactIdentity.split('@')[1];
    switch(source){
        case 'wa.gw.msging.net':
            return "Whatsapp";
        case '0mn.io':
            return "Site";
        case 'messenger.gw.msging.net':
            return "Facebook";
        case 'instagram.gw.msging.net':
            return "Instagram";
        case 'skype.gw.msging.net':
            return "Skype";
        case 'telegram.gw.msging.net':
            return "Telegram";
        case 'abs.gw.msging.net':
            return "MS Teams";
        default:
            return "Other";
    }
}

function run(contact, platform){
    contact = JSON.parse(contact);
    var telefone = "";

    // Tenta extrair o telefone se a origem for "Whatsapp"
    if(platform == "Whatsapp"){
        var telefone = contact.identity.split('@', 1)[0]; //retira o n¬∫ da string
    }
    // Tenta extrair o telefone j√° informado, ou porque a origem √© "Site" e ele foi passado na instancia√ß√£o javascript
    // do Blip Chat Widget, ou porque ele foi definido no contato em uma passagem anterior no fluxo pelo usu√°rio
    else if(contact.phoneNumber){
        var telefone = contact.phoneNumber;
    }

    if(telefone){
        var tam_tel = telefone.length;
        // Celular completo (DDI, DDD e '9')
        if(tam_tel == 13){
            var ddi = telefone.slice(0, 2);
            var ddd = telefone.slice(2, 4);
            var num = telefone.slice(4);

            telefone = ddd+num;
        }
        // Celular incompleto (DDI, DDD e faltando '9') ou Fixo completo (DDI e DDD)
        else if(tam_tel == 12){
            var ddi = telefone.slice(0, 2);
            var ddd = telefone.slice(2, 4);
            var num = telefone.slice(4);
            var movel = ((num[0] <= 9) && (num[0] >= 6));

            telefone = (movel) ? ddd+'9'+num : ddd+num;
        }
        // Celular incompleto (DDD e '9', mas falta o DDI)
        else if(tam_tel == 11){
            telefone = telefone;
        }
        // Celular incompleto (DDD, sem o '9' e sem DDI) ou Fixo incompleto (s√≥ DDD)
        else if(tam_tel == 10){
            var ddd = telefone.slice(0, 2);
            var num = telefone.slice(2);
            var movel = ((num[0] <= 9) && (num[0] >= 6));

            telefone = (movel) ? ddd+'9'+num : ddd+num;
        }
    }

    return telefone; //tel sem DDI
}

function run(contactIdentity){
    return contactIdentity.split('@', 1)[0];
}

function run(statusCode, response){
    if(statusCode != 200){
        return '';
    }

    response = JSON.parse(response);

    return (response.resource && response.resource.fullName) ? response.resource.fullName : '';
}

function run(statusCode, response) {
    if (statusCode != 200){
        return '';
    }

    response = JSON.parse(response);

    return (response.name) ? response.name : '';
}

function run(username, hour, usuarioRecorrente){
    var greetings = 'Boa noite';
    if((hour >= 3) && (hour < 15)){ //horario GMT (BR Time √© -3 GMT)
        greetings = 'Bom dia';
    }
    else if((hour >= 15) && (hour < 21)){ //horario GMT (BR Time √© -3 GMT)
        greetings = 'Boa tarde';
    }
    greetings += (username) ? `, ${username}, ` : `, `;

    // Texto complementar
    if(usuarioRecorrente == 'true'){
        greetings += 'seja bem vindo(a) de volta a Unipac!!! \n√â um prazer recebermos seu contato, estamos  aqui para ajudar!';
    }
    else{
        greetings +=  'seja bem vindo(a) a Unipac!!! \n√â um prazer recebermos seu contato, estamos aqui para ajudar!';
    }

    return greetings;
}

function run(platform, nome){
    /*******************************************************************************************************\
