    var texto =
    `${nome}, por gentileza com qual √°rea deseja falar? `+
    "\nLembre-se de que, a qualquer momento, voc√™ <b>pode usar</b> os comandos <b>#menu</b> ou <b>#sair</b>.";
    var opcoes = [
        {
            "value": "Comercial",
            "match": ['comerci'],
            "description": ""
        },
        {
            "value": "Financeiro",
            "match": ['financ'],
            "description": ""
        },
        {
            "value": "Envio de Curr√≠culo",
            "match": ['envi', 'curricu'],
            "description": ""
        },
        {
            "value": "Sugest√µes/Reclama√ß√µes",
            "match": ['sugest', 'reclama'],
            "description": ""
        }
    ];

    /*******************************************************************************************************\
     * Exclusivo textMenu = true                                                                           *
    \*******************************************************************************************************/
    var textMenu = false; // true -> (Exibe o menu como texto sempre), false -> (exibe normalmente como bot√µes/lista)
    var textExplain = `Digite o n√∫mero correspondente a sua op√ß√£o.`;
    var textExplainButton = `Se preferir, pode clicar no bot√£o abaixo e retornar ao menu principal.`;
    var textExplainButtonOptions = ['#menu'];

    /*******************************************************************************************************\
     * Exclusivo Whatsapp                                                                                  *
    \*******************************************************************************************************/
    /*************************************************************************\
     * O Whatsapp cont√©m as seguintes limita√ß√µes na implmenta√ß√£o dos menus:  *
     * - M√°x. de 60 caracteres para o header e o footer                      *
     * - M√°x. de 1024 caracteres para o texto(body) da pergunta              *
