    var opcoesWhatsApp = opcoes;
    var textExplainButtonOptionsWhatsApp = textExplainButtonOptions;


    /***                                                                                                          ***
     * --------------- N√ÉO ALTERE NADA A PARTIR DESSE PONTO, A MENOS QUE SAIBA O QUE EST√Å FAZENDO ----------------- *
     ***                                                                                                          ***/

    let param = {
        platform,
        texto,
        opcoes,
        opcoesWhatsApp,
        textExplain,
        textExplainButton,
        textExplainButtonOptions,
        textExplainButtonOptionsWhatsApp,
