function run(response){
    try {
        response = JSON.parse(response);
        if (response.exception && response.exception.message && response.exception.message == "Sucesso") {
            if (response.customer && response.customer.cpf) {
                return "CPF";
            }
            else if (response.customer && response.customer.cnpj) {
                return "CNPJ";
            }
        }

        return "Falha";
    }
    catch(e) {
        return "Falha";
    }
}

