function run(inputInfo) {
	inputInfo = JSON.parse(inputInfo);
	filtro = inputInfo.inputMatch;
	try {
		// Data atual
		let dataAtual = new Date();

		// Vari√°vel para armazenar a data anterior
		let dataAnterior = new Date();

		// Verificar o valor do filtro
		if (filtro === '15' || filtro === '30' || filtro === '90') {
			// Se o filtro for 15, 30 ou 90, subtrai o n√∫mero de dias correspondente
			dataAnterior.setDate(dataAnterior.getDate() - parseInt(filtro));

			// Formata√ß√£o das datas
			let dataAtualFormatada = `${dataAtual.getFullYear()}-${('0' + (dataAtual.getMonth() + 1)).slice(-2)}-${('0' + dataAtual.getDate()).slice(-2)}`;
			let dataAnteriorFormatada = `${dataAnterior.getFullYear()}-${('0' + (dataAnterior.getMonth() + 1)).slice(-2)}-${('0' + dataAnterior.getDate()).slice(-2)}`;

			// Se for 15, 30 ou 90, retorna o JSON com as datas
			return {
				"tipoOpe": "L",
				"idFiscal": "{{contact.taxDocument}}",
				"datDoc": dataAnteriorFormatada,
				"datDocFin": dataAtualFormatada
			};
		}
		else {
			// Se for um n√∫mero diferente, retorna o JSON sem as datas
			return {
				"tipoOpe": "L",
				"idFiscal": "{{contact.taxDocument}}"
			};
		}
	} catch (error) {
		// Em caso de erro, retorna "erro"
		return "erro";
	}
}

function run(response) {
    try {
        const data = JSON.parse(response);
        // Verificar se 'danfe' existe, √© um array, e tem o primeiro elemento com 'numDoc'
        if (Array.isArray(data.danfe) && data.danfe.length > 0 && data.danfe[0].numDoc) {
            return data.danfe[0].numDoc;
        }
    } catch (error) {
        // Nenhuma a√ß√£o √© necess√°ria aqui, pois qualquer erro resultar√° em "falha"
    }

    return "falha"; // Retornar "falha" se n√£o conseguir extrair 'numDoc'
}

function run(response) {
    try {
        const data = JSON.parse(response);
        if (Array.isArray(data.danfe) && data.danfe.length > 0) {
            const sortedDanfe = data.danfe.sort((a, b) => new Date(b.datDoc) - new Date(a.datDoc));

            const options = sortedDanfe.slice(0, 10).map(doc => {
                // Converter a data de 'datDoc' de 'aaaa-mm-dd' para 'dd-mm-aaaa'
                const formattedDate = doc.datDoc.split('-').reverse().join('/');

                return {
                    value: `NF ${doc.numDoc}`,
                    match: [`NF ${doc.numDoc}`],
                    description: `Vencimento: ${formattedDate}`,  // Data formatada
                    objref: doc
                };
            });

            return options;
        }
    } catch (error) {
        // Em caso de erro
    }

    return "falha"; // Retorna "falha" se n√£o for poss√≠vel processar
}

